Motif.Encryption.Rc4={encode:function(o,r){for(var n=[],e=[],t=0;256>t;t++)n[t]=t;var t=0,c=0,d=0,f=0,h=0;for(t=0;256>t;t++)c=(c+n[t]+r.charCodeAt(t%r.length))%256,h=n[t],n[t]=n[c],n[c]=h;for(t=256,c=256,d=0;d<o.length;d++)t=(t+1)%256,c=(c+n[t])%256,h=n[t],n[t]=n[c],n[c]=h,f=(n[t]+n[c])%256,e.push(String.fromCharCode(o.charCodeAt(d)^n[f]));return e.join("")},decode:function(o,r){return this.encode(o,r)}};